mapper.select:
SELECT t1.diseaseCode, t1.diseaseName, t1.sexLimit, t1.curativeEffect, t1.helpRememberCode , t1.createTime , t3.diseaseTypeId , t3.diseaseTypeName , t4.deseaseBodyPartId , t4.deseaseBodyPartName FROM meta_e_icd_10 t1 , meta_r_disease_type_icd_10 t2 , meta_c_disease_type t3 , meta_e_disease_body_part t4 WHERE LOCATE(t2.diseaseCode,t1.diseaseCode) AND t2.diseaseTypeId=t3.diseaseTypeId AND t3.diseaseBodyPartId=t4.deseaseBodyPartId
AND t1.createTime>#{createTime}
AND t1.diseaseCode=#{diseaseCode}
AND t3.diseaseTypeId=#{diseaseTypeId}
AND t4.deseaseBodyPartId=#{deseaseBodyPartId}
