choose.when:
and exists (
	select 1 from user_db.r_doctor_patient dp where dp.doctorId=#{operator} and f.author=dp.patientId
)

and exists (
	select 1 from user_db.r_doctor_patient dp where dp.doctorId=#{operator} and f.author=dp.patientId
)

mapper.select:
select f.contentTemplateId, f.contentTemplateName, f.contentTemplateType, f.content, f.needReply, f.isPublish, CASE WHEN (SELECT r.id FROM r_followup_task_content_template r WHERE r.contentTemplateId=f.contentTemplateId LIMIT 1) IS NULL AND (SELECT q.questionnaireId FROM questionnaire q WHERE q.contentTemplateId=f.contentTemplateId LIMIT 1) IS NULL AND (SELECT r.reqId FROM followup_wx_req r WHERE r.templateId=f.contentTemplateId LIMIT 1) IS NULL THEN 0 ELSE 1 END isInUsage from followup_content_template f where 1=1
and f.contentTemplateName like "%${contentTemplateName}%"
and f.contentTemplateType = #{contentTemplateType}
and f.needReply = #{needReply}
and f.contentTemplateId in (select contentTemplateId from r_followup_task_content_template where followupTaskId=#{followupTaskId})
and f.author = #{author}
order by f.createTime desc

	select count(1) from followup_content_template 
    where 1=1

        and contentTemplateName = #{contentTemplateName}


        and contentTemplateType = #{contentTemplateType}


        and needReply = #{needReply}
